import Tab from 'react-bootstrap/Tab';
import Tabs from 'react-bootstrap/Tabs';
import Cards from '../Card/Cards';

function ItemsTab(props) {
  return (
    <Tabs style={{marginLeft: '5%', marginRight:'5%'}}
      variant='pills'
      defaultActiveKey="1"
      id="justify-tab-example"
      className="mb-3"
      justify={true}
    >
     props.menu.map(item => (item.category === props.category));


      <Tab eventKey="1" title="Pizza's">
        <Cards menu={props.menu} category={'pizza'}/>
      </Tab>
      <Tab eventKey="2" title="Chinese">
        <Cards menu={props.menu} category={'chinese'} />
      </Tab>
      <Tab eventKey="3" title="South Indian">
        <Cards menu={props.menu} category={'southIndian'}/>
      </Tab>
      <Tab eventKey="4" title="Burger">
        <Cards menu={props.menu} category={'burger'}/>
      </Tab>
      <Tab eventKey="5" title="Fish">
        <Cards menu={props.menu} category={'fish'}/>
      </Tab>
    </Tabs>
  );
}

export default ItemsTab;
