import React, { useState } from 'react';
import { Col, Container, Row } from 'react-bootstrap';
import Carousel from 'react-bootstrap/Carousel';
import Badge from 'react-bootstrap/Badge';
import "./PhotoGallery.css"
function PhotoGallery(props) {
    const [index, setIndex] = useState(0);


    const handleSelect = (selectedIndex, e) => {
        setIndex(selectedIndex);
    };

    return (
        <Container style={{ height: "65%" }}>

            <Row xs={1} md={2}>
                <Col>
                    <div className='container' style={{ marginTop: "2%", marginBottom: "2%" }}>
                        <Carousel activeIndex={index} onSelect={handleSelect} style={{ height: "65%", width: "80%" }}>
                            {
                                props.restobject.restImages.map((imgSource) => {
                                    return (
                                        <Carousel.Item>
                                            <Image 
                                                className="d-block w-100 fluid"
                                                src={imgSource}
                                                alt="First slide"
                                                style={{ height: "10%", width: "65%" }}
                                            />
                                        </Carousel.Item>
                                    );
                                })
                            }
                        </Carousel>

                    </div>
                </Col>
                <Col>
                    <div className="container" style={{marginTop: "2%", marginBottom: "2%" }}>
                        <h2 style={{color: "white"}}>{props.restobject.restName}</h2>
                        <p>{props.restobject.address}</p>
                        <p>Timings: {props.restobject.startTime} AM - {props.restobject.endTime} PM</p>
                        <div>
                            {
                                props.restobject.categories.map((category) => {
                                    return (<Badge style={{marginRight: "1%", fontSize: "100%"}} pill bg="secondary">
                                    {category}
                                  </Badge>);
                                })
                            }
                        </div>
                    </div>
                </Col>
            </Row>
        </Container >


    );
}


export default PhotoGallery;
