import Tab from 'react-bootstrap/Tab';
import Tabs from 'react-bootstrap/Tabs';
import Cards from '../Card/Cards';

function ItemsTab(props) {

  function getCategories(p) {
    var categoryList = [...(p.menu.map(item => item.category))];
    var arr = [];
    for (var i = 0; i < categoryList.length; i++) {
      if (arr.indexOf(categoryList[i]) < 0) {
        arr.push(categoryList[i]);
      }
    } 
    for(let index = 0; index < arr.length; index++) {
        props.map((v, idx) => (
          <Tab key={idx} eventKey={idx} title={v.category}>
            <Cards menu={props.menu} category={v.category} />
          </Tab>
        ))
    }
    return arr; 
  }



  return (
    <Tabs style={{ marginLeft: '5%', marginRight: '5%' }}
      variant='pills'
      defaultActiveKey="1"
      id="justify-tab-example"
      className="mb-3"
      justify={true}
    >
      {
       getCategories(props)
      }
    </Tabs>
  );
}

export default ItemsTab;
