import { Col, Container, Row, Button } from 'react-bootstrap';

const Menu = (props) => {
    <Container style={{ height: "65%" }}>
        <Row xs={1} md={2}>
            <Col>
                <div className='container' style={{ marginTop: "2%", marginBottom: "2%", height: "100%", width: "100%" }}>

                    <Image
                        style={{ height: "350px" }}
                        src={props.restobject.image}
                        height={'200px'}
                        width={"200px"}
                    />


                </div>
            </Col>
            <Col>
                <div className="container" style={{ marginTop: "2%", marginBottom: "2%" }}>
                    <h2 style={{ color: "white" }}>{props.restobject.name}</h2>
                    <p>Price: ${props.restobject.price}</p>
                    <p>{props.restobject.description}</p>
                    <Badge pill bg="secondary">
                        {props.restobject.category}
                    </Badge>
                    <Button variant="success">Add To Cart</Button>
                </div>
            </Col>
        </Row>

    </Container >

}

export default Menu;



